import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, Box } from '@mui/material';
import { ArrowBack, ArrowForward } from '@mui/icons-material';

// Sample data for cards
const cards = [
  { id: 1, title: 'Card 1', description: 'This is the first card' },
  { id: 2, title: 'Card 2', description: 'This is the second card' },
  { id: 3, title: 'Card 3', description: 'This is the third card' },
  { id: 4, title: 'Card 4', description: 'This is the fourth card' },
  { id: 5, title: 'Card 5', description: 'This is the fifth card' },
];

const CardSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Move to the next card
  const nextCard = () => {
    if (currentIndex < cards.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      setCurrentIndex(0); // Loop back to the first card
    }
  };

  // Move to the previous card
  const prevCard = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    } else {
      setCurrentIndex(cards.length - 1); // Loop back to the last card
    }
  };

  return (
    <Box sx={{ width: '80%', margin: 'auto', padding: 4, textAlign: 'center' }}>
      {/* Navigation buttons */}
      <Button onClick={prevCard} variant="contained" color="primary" sx={{ position: 'absolute', left: '10px', top: '50%', transform: 'translateY(-50%)' }}>
        <ArrowBack />
      </Button>

      {/* Card Display */}
      <Card sx={{ maxWidth: 345, margin: '0 auto', padding: 2 }}>
        <CardContent>
          <Typography variant="h5" component="div">
            {cards[currentIndex].title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {cards[currentIndex].description}
          </Typography>
        </CardContent>
      </Card>
      <Card sx={{ maxWidth: 345, margin: '0 auto', padding: 2 }}>
        <CardContent>
          <Typography variant="h5" component="div">
            {cards[currentIndex].title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {cards[currentIndex].description}
          </Typography>
        </CardContent>
      </Card>

      {/* Navigation buttons */}
      <Button onClick={nextCard} variant="contained" color="primary" sx={{ position: 'absolute', right: '10px', top: '50%', transform: 'translateY(-50%)' }}>
        <ArrowForward />
      </Button>
    </Box>
  );
};

export default CardSlider;
